import { ACTIVE_BRAND, BRAND_LIST } from "../constants";

const INITIAL_STATE = {
    activeBrandId: '0',
    brandList: {}
}

const Reducer = (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case ACTIVE_BRAND:
            return {
                ...state,
                activeBrandId: action.brandId,
            };
        case BRAND_LIST:
            return {
                ...state,
                brandList: action.brandList
            };

        default:
            return state;
    }
}

export default Reducer
