import React from "react"
import logoImage from "../../assets/images/xotic-logo.png";
import bannerImage from "../../assets/images/login-Banner.png";

import { Formik, Field, Form, ErrorMessage } from "formik";
import * as Yup from "yup";

const Login = (props) => {
    return <>
        <div class="bodyClone loginPage">
            <section class="loginBody">
                <div class="loginForm">
                    <img src={logoImage} alt="logo" class="logo" />
                    <h1>Good afternoon Log into Xotic Labs</h1>
                    <Formik

                        initialValues={{
                            email: "om@g.com",
                            password: "123123123",
                        }}
                        validationSchema={Yup.object().shape({
                            email: Yup.string().required("Email is required"),
                            password: Yup.string().required("Password is required"),
                        })}
                        onSubmit={(
                            { username, password },
                            { setStatus, setSubmitting }
                        ) => {
                            setStatus();
                            AuthenticationService.login(username, password).then(
                                (user) => {
                                    const { from } = props.location.state || {
                                        from: { pathname: "/" },
                                    };
                                    props.history.push(from);

                                },
                                (error) => {
                                    // console.log(error);
                                    setSubmitting(false);
                                    setStatus(error);
                                }
                            );
                        }}

                    />

                    <form action="">
                        <div class="inputFields">
                            <div class="label">
                                <label for="email">Email address</label>
                            </div>
                            <input id="email" type="text" placeholder="Email" />
                        </div>
                        <div class="inputFields">
                            <div class="label">
                                <label for="password">Password</label>
                                <span>Forgot your password?</span>
                            </div>
                            <input id="password" type="password" placeholder="Password" />
                        </div>
                        <button type="submit">LOG IN</button>
                        <p class="ct-signUp">Don’t have an account yet? <span>Sign up</span></p>
                    </form>
                    <p class="copyright">Copyright © 2021 Xotic Labs</p>
                </div>
                <img src={bannerImage} alt="logo" class="bg-img" />
            </section>
        </div>
    </>
}

export default Login
