import React from "react"
import * as Yup from "yup";
import { Formik, Field, Form, ErrorMessage } from "formik";
import { Link } from "react-router-dom";
import { FormDataHelper } from '../../jwt/_helpers/FormDataHelper';
import { GeneralServices } from "../../jwt/_services/General.services";
import { BehaviorSubject } from "rxjs";

import logoImage from "../../assets/images/xotic-logo.png";
import bannerImage from "../../assets/images/login-Banner.png";
import { CLIENT_LOGIN } from "../../jwt/_services/axiousURL";


const currentUserSubject = new BehaviorSubject(
    JSON.parse(localStorage.getItem("currentUser"))
);

const Login = (props) => {
    return <>
        <div className="bodyClone loginPage">
            <section className="loginBody">
                <div className="loginForm">
                    <img src={logoImage} alt="logo" className="logo" />
                    <h1>Good afternoon Log into Xotic Labs</h1>
                    <Formik

                        initialValues={{
                            email: "client@xoticlabs.com",
                            password: "123",
                        }}
                        validationSchema={Yup.object().shape({
                            email: Yup.string().required("Email is required"),
                            password: Yup.string().required("Password is required"),
                        })}
                        onSubmit={(
                            { email, password },
                            { setStatus, setSubmitting }
                        ) => {
                            setStatus();
                            var formData = FormDataHelper();
                            formData.append('email_address', email);
                            formData.append('password', password);

                            GeneralServices.postRequest(formData, CLIENT_LOGIN).then(
                                (successResponse) => {
                                    const user = successResponse.data[0];
                                    localStorage.setItem("currentUser", JSON.stringify(user));
                                    currentUserSubject.next(user);

                                    const { from } = props.location.state || {
                                        from: { pathname: "/" },
                                    };
                                    props.history.push(from);
                                },
                                (error) => {
                                    setSubmitting(false);
                                    setStatus(error);
                                }
                            );
                        }}

                        render={({ errors, status, touched, isSubmitting }) => (
                            <Form>
                                <div className="inputFields">
                                    <div className="label">
                                        <label htmlFor="email">Email address</label>
                                    </div>
                                    <Field name="email" id="email" type="text" placeholder="Email" />
                                    <ErrorMessage
                                        name="email"
                                        component="div"
                                        className="invalid-feedback"
                                    />
                                </div>
                                <div className="inputFields">
                                    <div className="label">
                                        <label htmlFor="password">Password</label>
                                        <a href="#">Forgot your password?</a>
                                    </div>
                                    <Field name="password" id="password" type="password" placeholder="Password" />
                                    <ErrorMessage
                                        name="password"
                                        component="div"
                                        className="invalid-feedback"
                                    />
                                </div>
                                <button type="submit" disabled={isSubmitting}>LOG IN</button>
                                <p className="ct-signUp">Don’t have an account yet? <Link to={`/Signup`}>Sign Up</Link></p>

                            </Form>
                        )}
                    />
                    <p className="copyright">Copyright © 2021 Xotic Labs</p>
                </div>
                <img src={bannerImage} alt="logo" className="bg-img" />
            </section>
        </div>
    </>
}

export default Login
